        </node>
        <node TEXT="有M个参数的模型" ID="BRu7iyTxhi" _mubu_text="%3Cspan%3E%E6%9C%89%3C/span%3E%3Cspan%20class=%22italic%22%3EM%3C/span%3E%3Cspan%3E%E4%B8%AA%E5%8F%82%E6%95%B0%E7%9A%84%E6%A8%A1%E5%9E%8B%3C/span%3E" STYLE="fork">
          <node TEXT="以对每个参数进⾏类似的近似，假设所有的参数的∆w后验/∆w先验都相同" ID="JU5cwjTsva" _mubu_text="%3Cspan%3E%E4%BB%A5%E5%AF%B9%E6%AF%8F%E4%B8%AA%E5%8F%82%E6%95%B0%E8%BF%9B%E2%BE%8F%E7%B1%BB%E4%BC%BC%E7%9A%84%E8%BF%91%E4%BC%BC%EF%BC%8C%E5%81%87%E8%AE%BE%E6%89%80%E6%9C%89%E7%9A%84%E5%8F%82%E6%95%B0%E7%9A%84%E2%88%86w%E5%90%8E%E9%AA%8C/%E2%88%86w%E5%85%88%E9%AA%8C%E9%83%BD%E7%9B%B8%E5%90%8C%3C/span%3E" STYLE="fork"/>
          <node TEXT="【复杂度惩罚项】的⼤⼩随着模型中可调节参数M的数量线性增加。" ID="DGTPe5tXcH" _mubu_text="%3Cspan%20class=%22bold%20text-color-green%22%3E%E3%80%90%E5%A4%8D%E6%9D%82%E5%BA%A6%E6%83%A9%E7%BD%9A%E9%A1%B9%E3%80%91%3C/span%3E%3Cspan%3E%E7%9A%84%E2%BC%A4%E2%BC%A9%E9%9A%8F%E7%9D%80%E6%A8%A1%E5%9E%8B%E4%B8%AD%E5%8F%AF%E8%B0%83%E8%8A%82%E5%8F%82%E6%95%B0%3C/span%3E%3Cspan%20class=%22bold%20italic%20text-color-green%22%3EM%3C/span%3E%3Cspan%3E%E7%9A%84%E6%95%B0%E9%87%8F%E7%BA%BF%E6%80%A7%E5%A2%9E%E5%8A%A0%E3%80%82%3C/span%3E" STYLE="fork"/>
          <node TEXT="模型的复杂度增加" ID="NLkCrsfnlh" _mubu_text="%3Cspan%3E%E6%A8%A1%E5%9E%8B%E7%9A%84%E5%A4%8D%E6%9D%82%E5%BA%A6%E5%A2%9E%E5%8A%A0%3C/span%3E" STYLE="fork">
            <node TEXT="第⼀项通常会增⼤，因为⼀个更加复杂的模型能够更好地拟合数据" ID="GqTFfauHV9" _mubu_text="%3Cspan%3E%E7%AC%AC%E2%BC%80%E9%A1%B9%E9%80%9A%E5%B8%B8%E4%BC%9A%E5%A2%9E%E2%BC%A4%EF%BC%8C%E5%9B%A0%E4%B8%BA%E2%BC%80%E4%B8%AA%E6%9B%B4%E5%8A%A0%E5%A4%8D%E6%9D%82%E7%9A%84%E6%A8%A1%E5%9E%8B%E8%83%BD%E5%A4%9F%E6%9B%B4%E5%A5%BD%E5%9C%B0%E6%8B%9F%E5%90%88%E6%95%B0%E6%8D%AE%3C/span%3E" STYLE="fork"/>
            <node TEXT="第⼆项会减⼩，因为它依赖于M。" ID="KiU9OvX8tl" _mubu_text="%3Cspan%3E%E7%AC%AC%E2%BC%86%E9%A1%B9%E4%BC%9A%E5%87%8F%E2%BC%A9%EF%BC%8C%E5%9B%A0%E4%B8%BA%E5%AE%83%E4%BE%9D%E8%B5%96%E4%BA%8E%3C/span%3E%3Cspan%20class=%22italic%22%3EM%3C/span%3E%3Cspan%3E%E3%80%82%3C/span%3E" STYLE="fork"/>
          </node>
          <node TEXT="由最⼤模型证据确定的最优的模型复杂度需要在这两个相互竞争的项之间进⾏折中。" ID="7wCcq3OarP" _mubu_text="%3Cspan%3E%E7%94%B1%E6%9C%80%E2%BC%A4%E6%A8%A1%E5%9E%8B%E8%AF%81%E6%8D%AE%E7%A1%AE%E5%AE%9A%E7%9A%84%E6%9C%80%E4%BC%98%E7%9A%84%E6%A8%A1%E5%9E%8B%E5%A4%8D%E6%9D%82%E5%BA%A6%E9%9C%80%E8%A6%81%E5%9C%A8%E8%BF%99%E4%B8%A4%E4%B8%AA%E7%9B%B8%E4%BA%92%E7%AB%9E%E4%BA%89%E7%9A%84%E9%A1%B9%E4%B9%8B%E9%97%B4%E8%BF%9B%E2%BE%8F%E6%8A%98%E4%B8%AD%E3%80%82%3C/span%3E" STYLE="fork"/>
          <node TEXT="边缘似然是倾向于选择中等复杂度的模型" ID="GMRYWzcBd7" _mubu_text="%3Cspan%3E%E8%BE%B9%E7%BC%98%E4%BC%BC%E7%84%B6%E6%98%AF%E5%80%BE%E5%90%91%E4%BA%8E%E9%80%89%E6%8B%A9%E4%B8%AD%E7%AD%89%E5%A4%8D%E6%9D%82%E5%BA%A6%E7%9A%84%E6%A8%A1%E5%9E%8B%3C/span%3E" STYLE="fork">
            <node TEXT="三个模型M1,M2和M3，复杂度依次增加" ID="JTYbMVePwI" _mubu_text="%3Cspan%3E%E4%B8%89%E4%B8%AA%E6%A8%A1%E5%9E%8B%3C/span%3E%3Cspan%20class=%22italic%22%3EM%3C/span%3E%3Cspan%3E1%3C/span%3E%3Cspan%20class=%22italic%22%3E,M%3C/span%3E%3Cspan%3E2%E5%92%8C%3C/span%3E%3Cspan%20class=%22italic%22%3EM%3C/span%3E%3Cspan%3E3%EF%BC%8C%E5%A4%8D%E6%9D%82%E5%BA%A6%E4%BE%9D%E6%AC%A1%E5%A2%9E%E5%8A%A0%3C/span%3E" STYLE="fork"/>
            <node TEXT="横轴是可能的数据集构成的空间的⼀个⼀维表⽰，轴上的每个点都对应着⼀个具体的数据集" ID="wNz01evHRM" _mubu_text="%3Cspan%3E%E6%A8%AA%E8%BD%B4%E6%98%AF%E5%8F%AF%E8%83%BD%E7%9A%84%E6%95%B0%E6%8D%AE%E9%9B%86%E6%9E%84%E6%88%90%E7%9A%84%E7%A9%BA%E9%97%B4%E7%9A%84%E2%BC%80%E4%B8%AA%E2%BC%80%E7%BB%B4%E8%A1%A8%E2%BD%B0%EF%BC%8C%E8%BD%B4%E4%B8%8A%E7%9A%84%E6%AF%8F%E4%B8%AA%E7%82%B9%E9%83%BD%E5%AF%B9%E5%BA%94%E7%9D%80%E2%BC%80%E4%B8%AA%E5%85%B7%E4%BD%93%E7%9A%84%E6%95%B0%E6%8D%AE%E9%9B%86%3C/span%3E" STYLE="fork"/>
            <node TEXT="假设让这三个模型产生样本数据集，然后观察这些数据集的分布。由于参数由先验概率分布控制，且对于任意一种参数的选择都可能在目标变量上增加一个随机噪声，所以任意给定的模型都能够生成一系列不同的数据集。为了从具体的模型中生成一个特定的数据集，我们首先从先验分布p(w)中选择参数的值，然后对于这些参数的值我们按概率p(D|w)对数据进行采样。" ID="Ij7tqlBfdb" _mubu_text="%3Cspan%3E%E5%81%87%E8%AE%BE%E8%AE%A9%E8%BF%99%E4%B8%89%E4%B8%AA%E6%A8%A1%E5%9E%8B%3C/span%3E%3Cspan%20class=%22bold%20text-color-yellow%22%3E%E4%BA%A7%E7%94%9F%E6%A0%B7%E6%9C%AC%E6%95%B0%E6%8D%AE%E9%9B%86%3C/span%3E%3Cspan%3E%EF%BC%8C%E7%84%B6%E5%90%8E%E8%A7%82%E5%AF%9F%E8%BF%99%E4%BA%9B%E6%95%B0%E6%8D%AE%E9%9B%86%E7%9A%84%E5%88%86%E5%B8%83%E3%80%82%E7%94%B1%E4%BA%8E%E5%8F%82%E6%95%B0%E7%94%B1%E5%85%88%E9%AA%8C%E6%A6%82%E7%8E%87%E5%88%86%E5%B8%83%E6%8E%A7%E5%88%B6%EF%BC%8C%E4%B8%94%E5%AF%B9%E4%BA%8E%E4%BB%BB%E6%84%8F%E4%B8%80%E7%A7%8D%E5%8F%82%E6%95%B0%E7%9A%84%E9%80%89%E6%8B%A9%E9%83%BD%E5%8F%AF%E8%83%BD%E5%9C%A8%E7%9B%AE%E6%A0%87%E5%8F%98%E9%87%8F%E4%B8%8A%E5%A2%9E%E5%8A%A0%E4%B8%80%E4%B8%AA%E9%9A%8F%E6%9C%BA%E5%99%AA%E5%A3%B0%EF%BC%8C%E6%89%80%E4%BB%A5%E4%BB%BB%E6%84%8F%E7%BB%99%E5%AE%9A%E7%9A%84%E6%A8%A1%E5%9E%8B%E9%83%BD%E8%83%BD%E5%A4%9F%3C/span%3E%3Cspan%20class=%22bold%20text-color-yellow%22%3E%E7%94%9F%E6%88%90%E4%B8%80%E7%B3%BB%E5%88%97%E4%B8%8D%E5%90%8C%E7%9A%84%E6%95%B0%E6%8D%AE%E9%9B%86%3C/span%3E%3Cspan%3E%E3%80%82%E4%B8%BA%E4%BA%86%E4%BB%8E%E5%85%B7%E4%BD%93%E7%9A%84%E6%A8%A1%E5%9E%8B%E4%B8%AD%E7%94%9F%E6%88%90%E4%B8%80%E4%B8%AA%E7%89%B9%E5%AE%9A%E7%9A%84%E6%95%B0%E6%8D%AE%E9%9B%86%EF%BC%8C%E6%88%91%E4%BB%AC%E9%A6%96%E5%85%88%E4%BB%8E%E5%85%88%E9%AA%8C%E5%88%86%E5%B8%83p(w)%E4%B8%AD%E9%80%89%E6%8B%A9%E5%8F%82%E6%95%B0%E7%9A%84%E5%80%BC%EF%BC%8C%E7%84%B6%E5%90%8E%E5%AF%B9%E4%BA%8E%E8%BF%99%E4%BA%9B%E5%8F%82%E6%95%B0%E7%9A%84%E5%80%BC%E6%88%91%E4%BB%AC%E6%8C%89%E6%A6%82%E7%8E%87p(D%7Cw)%E5%AF%B9%E6%95%B0%E6%8D%AE%E8%BF%9B%E8%A1%8C%E9%87%87%E6%A0%B7%E3%80%82%3C/span%3E" STYLE="fork"/>
            <node TEXT="一个简单的模型（例如，基于一阶多项式）几乎没有变化性，因此生成的数据集彼此之间都十分相似。因此它的分布p(D)被限制在横轴的一个相对小的区域。相反，一个复杂的模型（例如九阶多项式）可以生成变化性相当大的数据集，因此它的分布p(D)散布在数据集空间中的一个相当大的区域。" ID="8PBISYBhso" _mubu_text="%3Cspan%20class=%22bold%20text-color-yellow%22%3E%E4%B8%80%E4%B8%AA%E7%AE%80%E5%8D%95%E7%9A%84%E6%A8%A1%E5%9E%8B%3C/span%3E%3Cspan%3E%EF%BC%88%E4%BE%8B%E5%A6%82%EF%BC%8C%E5%9F%BA%E4%BA%8E%E4%B8%80%E9%98%B6%E5%A4%9A%E9%A1%B9%E5%BC%8F%EF%BC%89%E5%87%A0%E4%B9%8E%E6%B2%A1%E6%9C%89%E5%8F%98%E5%8C%96%E6%80%A7%EF%BC%8C%E5%9B%A0%E6%AD%A4%E7%94%9F%E6%88%90%E7%9A%84%E6%95%B0%E6%8D%AE%E9%9B%86%E5%BD%BC%E6%AD%A4%E4%B9%8B%E9%97%B4%E9%83%BD%3C/span%3E%3Cspan%20class=%22bold%20underline%22%3E%E5%8D%81%E5%88%86%E7%9B%B8%E4%BC%BC%3C/span%3E%3Cspan%3E%E3%80%82%E5%9B%A0%E6%AD%A4%E5%AE%83%E7%9A%84%E5%88%86%E5%B8%83p(D)%E8%A2%AB%E9%99%90%E5%88%B6%E5%9C%A8%E6%A8%AA%E8%BD%B4%E7%9A%84%E4%B8%80%E4%B8%AA%E7%9B%B8%E5%AF%B9%E5%B0%8F%E7%9A%84%E5%8C%BA%E5%9F%9F%E3%80%82%E7%9B%B8%E5%8F%8D%EF%BC%8C%3C/span%3E%3Cspan%20class=%22bold%20text-color-yellow%22%3E%E4%B8%80%E4%B8%AA%E5%A4%8D%E6%9D%82%E7%9A%84%E6%A8%A1%E5%9E%8B%3C/span%3E%3Cspan%3E%EF%BC%88%E4%BE%8B%E5%A6%82%E4%B9%9D%E9%98%B6%E5%A4%9A%E9%A1%B9%E5%BC%8F%EF%BC%89%E5%8F%AF%E4%BB%A5%E7%94%9F%E6%88%90%3C/span%3E%3Cspan%20class=%22bold%20underline%22%3E%E5%8F%98%E5%8C%96%E6%80%A7%E7%9B%B8%E5%BD%93%E5%A4%A7%3C/span%3E%3Cspan%3E%E7%9A%84%E6%95%B0%E6%8D%AE%E9%9B%86%EF%BC%8C%E5%9B%A0%E6%AD%A4%E5%AE%83%E7%9A%84%E5%88%86%E5%B8%83p(D)%E6%95%A3%E5%B8%83%E5%9C%A8%E6%95%B0%E6%8D%AE%E9%9B%86%E7%A9%BA%E9%97%B4%E4%B8%AD%E7%9A%84%E4%B8%80%E4%B8%AA%E7%9B%B8%E5%BD%93%E5%A4%A7%E7%9A%84%E5%8C%BA%E5%9F%9F%E3%80%82%3C/span%3E" STYLE="fork"/>
            <node TEXT="简单的模型不能很好的拟合数据，而更复杂的模型把它的预测概率散布于过多的可能的数据集当中，从而对它们当中的每一个赋予的概率都相对较小。" ID="d7FFBuow1y" _mubu_text="%3Cspan%3E%E7%AE%80%E5%8D%95%E7%9A%84%E6%A8%A1%E5%9E%8B%E4%B8%8D%E8%83%BD%E5%BE%88%E5%A5%BD%E7%9A%84%E6%8B%9F%E5%90%88%E6%95%B0%E6%8D%AE%EF%BC%8C%E8%80%8C%E6%9B%B4%E5%A4%8D%E6%9D%82%E7%9A%84%E6%A8%A1%E5%9E%8B%E6%8A%8A%E5%AE%83%E7%9A%84%E9%A2%84%E6%B5%8B%E6%A6%82%E7%8E%87%E6%95%A3%E5%B8%83%E4%BA%8E%E8%BF%87%E5%A4%9A%E7%9A%84%E5%8F%AF%E8%83%BD%E7%9A%84%E6%95%B0%E6%8D%AE%E9%9B%86%E5%BD%93%E4%B8%AD%EF%BC%8C%E4%BB%8E%E8%80%8C%E5%AF%B9%E5%AE%83%E4%BB%AC%E5%BD%93%E4%B8%AD%E7%9A%84%E6%AF%8F%E4%B8%80%E4%B8%AA%E8%B5%8B%E4%BA%88%E7%9A%84%E6%A6%82%E7%8E%87%E9%83%BD%E7%9B%B8%E5%AF%B9%E8%BE%83%E5%B0%8F%E3%80%82%3C/span%3E" STYLE="fork"/>
          </node>
        </node>
      </node>
    </node>
    <node TEXT="3.5 The Evidence Approximation（证据近似）" ID="ymPQG6arR2" _mubu_text="%3Cspan%20class=%22bold%22%3E3.5%20The%20Evidence%20Approximation%EF%BC%88%E8%AF%81%E6%8D%AE%E8%BF%91%E4%BC%BC%EF%BC%89%3C/span%3E" STYLE="bubble" POSITION="default">
      <node TEXT="3.5.0 引入" ID="3PIVXBhnDR" _mubu_text="%3Cspan%20class=%22bold%20text-color-green%22%3E3.5.0%20%E5%BC%95%E5%85%A5%3C/span%3E" STYLE="fork">
        <node TEXT="【问题】：在线性基函数模型的纯粹贝叶斯方法中，我们会引入超参数𝛼,𝛽的先验分布，然后通过对超参数以及参数𝑤求边缘化的方式来做预测。尽管我们可以解析的得到对𝑤的积分或对超参数的积分，但是求出对所有这些变量完整地边缘化的解析解是比较棘手的。" ID="0877CIjblk" _mubu_text="%3Cspan%3E%E3%80%90%E9%97%AE%E9%A2%98%E3%80%91%EF%BC%9A%E5%9C%A8%E7%BA%BF%E6%80%A7%E5%9F%BA%E5%87%BD%E6%95%B0%E6%A8%A1%E5%9E%8B%E7%9A%84%3C/span%3E%3Cspan%20class=%22underline%22%3E%E7%BA%AF%E7%B2%B9%E8%B4%9D%E5%8F%B6%E6%96%AF%E6%96%B9%E6%B3%95%3C/span%3E%3Cspan%3E%E4%B8%AD%EF%BC%8C%E6%88%91%E4%BB%AC%E4%BC%9A%E5%BC%95%E5%85%A5%3C/span%3E%3Cspan%20class=%22underline%22%3E%E8%B6%85%E5%8F%82%E6%95%B0%F0%9D%9B%BC,%F0%9D%9B%BD%E7%9A%84%E5%85%88%E9%AA%8C%E5%88%86%E5%B8%83%3C/span%3E%3Cspan%3E%EF%BC%8C%E7%84%B6%E5%90%8E%E9%80%9A%E8%BF%87%3C/span%3E%3Cspan%20class=%22underline%22%3E%E5%AF%B9%E8%B6%85%E5%8F%82%E6%95%B0%E4%BB%A5%E5%8F%8A%E5%8F%82%E6%95%B0%F0%9D%91%A4%E6%B1%82%E8%BE%B9%E7%BC%98%E5%8C%96%E7%9A%84%E6%96%B9%E5%BC%8F%E6%9D%A5%E5%81%9A%E9%A2%84%E6%B5%8B%3C/span%3E%3Cspan%3E%E3%80%82%E5%B0%BD%E7%AE%A1%E6%88%91%E4%BB%AC%E5%8F%AF%E4%BB%A5%E8%A7%A3%E6%9E%90%E7%9A%84%E5%BE%97%E5%88%B0%E5%AF%B9%F0%9D%91%A4%E7%9A%84%E7%A7%AF%E5%88%86%E6%88%96%E5%AF%B9%E8%B6%85%E5%8F%82%E6%95%B0%E7%9A%84%E7%A7%AF%E5%88%86%EF%BC%8C%E4%BD%86%E6%98%AF%E6%B1%82%E5%87%BA%E5%AF%B9%E6%89%80%E6%9C%89%E8%BF%99%E4%BA%9B%E5%8F%98%E9%87%8F%E5%AE%8C%E6%95%B4%E5%9C%B0%E8%BE%B9%E7%BC%98%E5%8C%96%E7%9A%84%E8%A7%A3%E6%9E%90%E8%A7%A3%E6%98%AF%E6%AF%94%E8%BE%83%E6%A3%98%E6%89%8B%E7%9A%84%E3%80%82%3C/span%3E" STYLE="fork"/>
        <node TEXT="【证据近似（evidence approximation）】【解决】：" ID="WqxaNHmuYD" _mubu_text="%3Cspan%20class=%22bold%20text-color-yellow%22%3E%E3%80%90%E8%AF%81%E6%8D%AE%E8%BF%91%E4%BC%BC%EF%BC%88evidence%20approximation%EF%BC%89%E3%80%91%E3%80%90%E8%A7%A3%E5%86%B3%E3%80%91%EF%BC%9A%3C/span%3E" STYLE="fork">
